@@ -0,0 +1,200 @@
+* {
+  margin: 0;
+  padding: 0;
+  box-sizing: border-box;
+}
+
+body {
+    text-align: center;
+    background: linear-gradient(135deg, #1a2a6c, #b21f1f, #1a2a6c);
+    background-size: 400% 400%;
+    animation: gradientBG 15s ease infinite;
+    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
+
+    /* REMOVE THESE ↓↓↓ */
+    /* min-height: 100vh; */
+    /* display: flex; */
+    /* flex-direction: column; */
+    /* align-items: center; */
+    /* justify-content: center; */
+
+    padding: 20px 0 0; /* Top padding only */
+    color: #ffffff;
+}
+
+@keyframes gradientBG {
+    0% { background-position: 0% 50%; }
+    50% { background-position: 100% 50%; }
+    100% { background-position: 0% 50%; }
+}
+
+h1 {
+    color: #ffffff;
+    margin: 10px 0 30px;
+    font-size: 3.5rem;
+    text-shadow: 0 2px 10px rgba(0,0,0,0.3);
+    letter-spacing: 2px;
+}
+
+.container {
+    margin-top: 0px; /* <- Add this to push content a bit down from top */
+    width: 100%;
+    max-width: 600px;
+    display: flex;
+    flex-direction: column;
+    align-items: center;
+}
+
+.game {
+    height: 60vmin;
+    width: 60vmin;
+    max-width: 500px;
+    max-height: 500px;
+    gap: 10px;
+    display: flex;
+    flex-wrap: wrap;
+    align-items: center;
+    justify-content: center;
+    margin: 0 auto 30px;
+    perspective: 1000px;
+}
+
+.box {
+    background: rgba(255, 255, 255, 0.95);
+    height: 18vmin;
+    width: 18vmin;
+    font-weight: bold;
+    font-size: 5rem;
+    border: none;
+    border-radius: 10px;
+    box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
+    cursor: pointer;
+    transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
+    color: #2c3e50;
+    display: flex;
+    align-items: center;
+    justify-content: center;
+}
+
+.box:hover {
+    transform: translateY(-5px) scale(1.05);
+    box-shadow: 0 12px 20px rgba(0, 0, 0, 0.3);
+    background: #ffffff;
+}
+
+.box:active {
+    transform: scale(0.95);
+}
+
+.box.x {
+    color: #e74c3c;
+}
+
+.box.o {
+    color: #3498db;
+}
+
+.reset-btn, .new-btn {
+    align-items: center;
+    padding: 15px 30px;
+    font-size: 1.1rem;
+    font-weight: 600;
+    background: rgba(255, 255, 255, 0.15);
+    color: white;
+    border: none;
+    border-radius: 50px;
+    cursor: pointer;
+    transition: all 0.3s ease;
+    backdrop-filter: blur(5px);
+    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
+    margin: 10px;
+    min-width: 150px;
+}
+
+.reset-btn:hover, .new-btn:hover {
+    background: rgba(255, 255, 255, 0.25);
+    transform: translateY(-3px);
+    box-shadow: 0 6px 20px rgba(0, 0, 0, 0.3);
+}
+
+.reset-btn:active, .new-btn:active {
+    transform: translateY(1px);
+}
+
+.reset-btn {
+    background: rgba(231, 76, 60, 0.7);
+}
+
+.reset-btn:hover {
+    background: rgba(231, 76, 60, 0.9);
+}
+
+.new-btn {
+    background: rgba(52, 152, 219, 0.7);
+}
+
+.new-btn:hover {
+    background: rgba(52, 152, 219, 0.9);
+}
+
+.msg-container {
+    height: auto;
+    padding: 20px;
+    background: rgba(255, 255, 255, 0.1);
+    border-radius: 15px;
+    backdrop-filter: blur(10px);
+    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.2);
+    transition: all 0.5s ease;
+    margin: 20px 0;
+}
+
+.msg {
+    padding: 10px;
+    font-size: 1.8rem;
+    font-weight: 600;
+    color: #ffffff;
+    text-shadow: 0 2px 5px rgba(0,0,0,0.3);
+}
+
+.hide {
+    opacity: 0;
+    transform: translateY(-20px);
+    pointer-events: none;
+}
+
+/* Add this for winning animation */
+.box.win {
+    animation: pulse 1.5s infinite;
+    background: #2ecc71;
+    color: white;
+}
+
+@keyframes pulse {
+    0% { transform: scale(1); }
+    50% { transform: scale(1.1); }
+    100% { transform: scale(1); }
+}
+
+/* Responsive adjustments */
+@media (max-width: 600px) {
+    h1 {
+        font-size: 2.5rem;
+    }
+    
+    .game {
+        height: 70vmin;
+        width: 70vmin;
+    }
+    
+    .box {
+        height: 20vmin;
+        width: 20vmin;
+        font-size: 4rem;
+    }
+    
+    .reset-btn, .new-btn {
+        padding: 12px 25px;
+        font-size: 1rem;
+        width: 80%;
+    }
+}
