commits.content:
*{
    margin: 0;
    padding: 0;
}

body{
    text-align: center;
    background-color: #1F3F49;
}
h1{
    color: #A4BDFC;
    margin-top: 10px;
    padding-bottom: 20px;
    font-size: 60px;
}
.container{
    text-align: center;
    height: 70vh;
    display: flex;
    justify-content: center;
}
.game
{
    height: 60vmin;
    width: 60vmin;
    gap: 1.5vmin;
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    justify-content: center;
}
.box{
    height: 18vmin;
    width: 18vmin;
    font-weight: bolder;
    font-size: xx-large;
    border: none;
    border-radius: 2vmin;
    box-shadow: 0 0 1rem rgb(0, 0, 1);
}
.box:hover {
  color: #fca5a5;
  transform: translateY(-2px);
}
.box:active {
transform: scale(0.98);
}
.btn{
    margin-top: -10vmin;
    align-items: center;
    padding: 1vmin;
    font-size: 2.5vmin;
    font-weight: bold;
    background-color: black;
    color: white;
    border-radius: 1vmin;
    width: 20vmin;
    height: 7vmin;
}
.btn:hover {
  color: #a5f3fc;
  transform: translateY(-2px);
}
.btn:active {
transform: scale(0.98);
}
